import React, { Component } from "react";
import "./App.css";
import axios from "axios";
import fantasyfootball2 from "./Components/fantasyfootball2.jpg";
import PlayerList from "./Components/PlayerList";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      // allPlayers: [],
      players: []
    };
    this.getPlayers = this.getPlayers.bind(this);
    this.postPlayer = this.postPlayer.bind(this);
  }

  componentDidMount() {
    // this.getAllPlayers();
    this.getPlayers();
  }

  // getAllPlayers() {
  getPlayers() {
    axios
      .get("/api/players")
      .then(response => {
        this.setState({
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  postPlayer(name, position, comments) {
    const newPlayer = {
      name,
      position,
      comments
    };
    axios
      .post(`/api/players`, newPlayer)
      .then(response => {
        this.setState({
          // allPlayers: response.data
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    const { players } = this.state;
    return (
      <div className="App">
        <header>
          <div>
            <h1>Waiver Wire Wizard</h1>
          </div>
          <div>
            <a href="#playerOne">#1</a>
            <a href="#playerTwo">#2</a>
            <a href="#playerThree">#3</a>
            <a href="#playerFour">#4</a>
            <a href="#playerFive">#5</a>
            <a href="#playerSix">#6</a>
            <a href="#playerSeven">#7</a>
            <a href="#playerEight">#8</a>
            <a href="#playerNine">#9</a>
            <a href="#playerTen">#10</a>
          </div>
        </header>
        {/* <div>{mappedPlayers}</div> */}
        <main className="main-container">
          <p>The website you shouldn't go to but can't help to...</p>
          <div className="link-icon-row">
            <img></img>
            <img></img>
            <img></img>
            <img></img>
          </div>
        </main>
        <div className="player-numbers">
          <div id="playerOne">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div id="playerTwo">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div id="playerThree">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div id="playerFour">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div id="playerFive">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div id="playerSix">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div id="playerSeven">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div id="playerEight">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div id="playerNine">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div id="playerTen">
            <img></img>
            <h3></h3>
            <span></span>
          </div>

          <ol>
            {players.map(player => {
              return (
                <li>
                  <div>{player.id}</div>
                  <div>{player.name}</div>
                  <div>{player.position}</div>
                  <div>{player.comments}</div>
                </li>
              );
            })}
          </ol>
        </div>
        <PlayerList postPlayer={this.postPlayer} />
      </div>
      // const { allPlayers } = this.state;
      // console.log(players);
      // const mappedPlayers = allPlayers.map(player => {
      //   return (
      //     <div key={player.id}>
      //       <div>{player.playerName}</div>
      //       <div>{player.name}</div>
      //       <div className="image-container">
      //       </div>
      //     </div>
      //   );
      // });
    );
  }
}

export default App;
