import React, { Component } from "react";
import "./App.css";
import axios from "axios";
import punisher from "./Components/punisher.png";
// import cowboys from "./Components/cowboys.png";
import PlayerList from "./Components/PlayerList";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allPlayers: [],
      players: []
    };
    this.getAllPlayers = this.getAllPlayers.bind(this);
    this.postPlayer = this.postPlayer.bind(this);
  }

  componentDidMount() {
    this.getAllPlayers();
  }

  getAllPlayers() {
    axios
      .get("/api/players")
      .then(response => {
        this.setState({
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  postPlayer(name, position, comments) {
    const newPlayer = {
      name,
      position,
      comments
    };
    axios
      .post(`/api/players`, newPlayer)
      .then(response => {
        this.setState({
          allPlayers: response.data
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    const { allPlayers, players } = this.state;
    console.log(players);
    const mappedPlayers = allPlayers.map(player => {
      return (
        <div key={player.id}>
          <h1>Waiver Wire Wizard</h1>
          <div>{player.playerName}</div>
          <div>{player.name}</div>
          <div className="image-container">
            <img src={punisher} alt="" />
          </div>
        </div>
      );
    });
    return (
      <div className="App">
        <div>{mappedPlayers}</div>

        {/* <img src={cowboys} /> */}

        <PlayerList postPlayer={this.postPlayer} />
        <div>
          <ol>
            {players.map(player => {
              return (
                <li>
                  <div>{player.name}</div>
                  <div>{player.position}</div>
                  <div>{player.comments}</div>
                </li>
              );
            })}
          </ol>
        </div>
      </div>
    );
  }
}

export default App;
