import React, { Component } from "react";
import "./App.css";
import axios from "axios";
import fantasyfootball2 from "./Components/fantasyfootball2.jpg";
import PlayerList from "./Components/PlayerList";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      // allPlayers: [],
      players: []
    };
    this.getPlayers = this.getPlayers.bind(this);
    this.postPlayer = this.postPlayer.bind(this);
  }

  componentDidMount() {
    // this.getAllPlayers();
    this.getPlayers();
  }

  // getAllPlayers() {
  getPlayers() {
    axios
      .get("/api/players")
      .then(response => {
        this.setState({
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  postPlayer(name, position, comments) {
    const newPlayer = {
      name,
      position,
      comments
    };
    axios
      .post(`/api/players`, newPlayer)
      .then(response => {
        this.setState({
          // allPlayers: response.data
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    const { players } = this.state;
    return (
      <div className="App">
        <header>
          <div>
            <h1>Waiver Wire Wizard</h1>
          </div>
          <div>
            <a href="numberOne">#1</a>
            <a href="numberTwo">#2</a>
            <a href="numberThree">#3</a>
            <a href="numberFour">#4</a>
            <a href="numberFive">#5</a>
            <a href="numberSix">#6</a>
            <a href="numberSeven">#7</a>
            <a href="numberEight">#8</a>
            <a href="numberNine">#9</a>
            <a href="numberTen">#10</a>
          </div>
        </header>
        {/* <div>{mappedPlayers}</div> */}
        <main>
          <img src={fantasyfootball2} alt="" />
        </main>
        <div>
          <ol>
            {players.map(player => {
              return (
                <li>
                  <div>{player.id}</div>
                  <div>{player.name}</div>
                  <div>{player.position}</div>
                  <div>{player.comments}</div>
                </li>
              );
            })}
          </ol>
        </div>
        <PlayerList postPlayer={this.postPlayer} />
      </div>
      // const { allPlayers } = this.state;
      // console.log(players);
      // const mappedPlayers = allPlayers.map(player => {
      //   return (
      //     <div key={player.id}>
      //       <div>{player.playerName}</div>
      //       <div>{player.name}</div>
      //       <div className="image-container">
      //       </div>
      //     </div>
      //   );
      // });
    );
  }
}

export default App;
