import React, { Component } from "react";
import "./App.css";
import axios from "axios";
import PlayerAdd from "./Components/PlayerAdd";
import EditButton from "./Components/EditButton";
// import DeleteButton from "./Components/DeleteButton";
// import PlayerEdit from "./Components/PlayerEdit";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      players: []
    };
    this.getPlayers = this.getPlayers.bind(this);
    this.postPlayer = this.postPlayer.bind(this);
    this.putPlayer = this.putPlayer.bind(this);
    // this.deletePlayer = this.deletePlayer.bind(this);
  }

  componentDidMount() {
    this.getPlayers();
  }

  getPlayers() {
    axios
      .get("/api/players")
      .then(response => {
        this.setState({
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  postPlayer(name, position, comments) {
    const newPlayer = {
      name,
      position,
      comments
    };
    axios
      .post(`/api/players`, newPlayer)
      .then(response => {
        this.setState({
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  putPlayer(name, position, comments) {
    const editPlayer = {
      name,
      position,
      comments
    };
    axios
      .put(`/api/players/${this.players.id}`, editPlayer)
      .then(response => {
        this.setState({
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    const { players } = this.state;
    return (
      <div className="App">
        <header>
          <div className="header-left">
            <h1>Waiver Wire Wizard</h1>
          </div>
          <div className="header-right">
            <a href="#playerOne">#1</a>
            <a href="#playerTwo">#2</a>
            <a href="#playerThree">#3</a>
            <a href="#playerFour">#4</a>
            <a href="#playerFive">#5</a>
            <a href="#playerSix">#6</a>
            <a href="#playerSeven">#7</a>
            <a href="#playerEight">#8</a>
            <a href="#playerNine">#9</a>
            <a href="#playerTen">#10</a>
          </div>
        </header>
        <main className="main-container">
          <p>The website you shouldn't go to but can't help to...</p>
          <div className="link-icon-row">
            <img></img>
            <img></img>
            <img></img>
            <img></img>
          </div>
        </main>
        <div className="player-numbers">
          <div className="playerOne" id="playerOne">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div className="playerTwo" id="playerTwo">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div className="playerThree" id="playerThree">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div className="playerFour" id="playerFour">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div className="playerFive" id="playerFive">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div className="playerSix" id="playerSix">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div className="playerSeven" id="playerSeven">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div className="playerEight" id="playerEight">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div className="playerNine" id="playerNine">
            <img></img>
            <h3></h3>
            <span></span>
          </div>
          <div className="playerTen" id="playerTen">
            <img></img>
            <h3></h3>
            <span></span>
          </div>

          <ol>
            {players.map((player, index) => {
              return (
                <li>
                  <div>
                    {player.name}, {player.position}
                  </div>
                  <div>{player.comments}</div>
                  <EditButton putPlayer={this.putPlayer} />
                  {/* <DeleteButton deletePlayer={this.deletePlayer}/> */}
                </li>
              );
            })}
          </ol>
        </div>
        <PlayerAdd postPlayer={this.postPlayer} />
        {/* <PlayerEdit putPlayer={this.putPlayer} /> */}
      </div>
    );
  }
}

export default App;
