import React, { Component } from "react";
import "./App.css";
import axios from "axios";
// import TopTenDiv from "./Components/TopTenDiv";
import PlayerList from "./Components/PlayerList";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      // allPlayers: [],
      players: []
    };
    this.getPlayers = this.getPlayers.bind(this);
    this.postPlayer = this.postPlayer.bind(this);
  }

  componentDidMount() {
    // this.getAllPlayers();
    this.getPlayers();
  }

  // getAllPlayers() {
  getPlayers() {
    axios
      .get("/api/players")
      .then(response => {
        this.setState({
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  postPlayer(name, position, comments) {
    let newPlayer = {
      name,
      position,
      comments
    };
    axios
      .post(`/api/players`, newPlayer)
      .then(response => {
        this.setState({
          // allPlayers: response.data
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  updatePlayer(name, position, comments) {
    let editPlayer = {
      name,
      position,
      comments
    };
    axios
      .put(`/api/players/:id`, editPlayer)
      .then(response => {
        this.setState({
          players: response.data
        });
      })
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div className="App">
        <header>
          <div className="header-left">
            <h1>
              <i>Waiver Wire Wizard</i>
            </h1>
          </div>
          <div className="header-right">
            <a href="#playerOne">#1</a>
            <a href="#playerTwo">#2</a>
            <a href="#playerThree">#3</a>
            <a href="#playerFour">#4</a>
            <a href="#playerFive">#5</a>
            <a href="#playerSix">#6</a>
            <a href="#playerSeven">#7</a>
            <a href="#playerEight">#8</a>
            <a href="#playerNine">#9</a>
            <a href="#playerTen">#10</a>
          </div>
        </header>
        <main className="main-container">
          <p>The website you shouldn't go to but can't help to...</p>
          <div className="link-icon-row">
            <img></img>
            <img></img>
            <img></img>
            <img></img>
          </div>
        </main>
        {/* <TopTenDiv /> */}
        <div>
          <PlayerList postPlayer={this.postPlayer} />
        </div>
      </div>
    );
  }
}

export default App;
